const char*
HXErrorCodeToString( UINT32 ulHXCode )
{
    int index;
    for ( index = 0; HXCodeToKeyMap[ index ].ulHXCode != 0; ++index )
    {
        if ( HXCodeToKeyMap[index].ulHXCode == ulHXCode )
        {
	    return HXCodeToKeyMap[ index ].pKeyString;
        }
    }
    return NULL;
}
